import styled from 'styled-components';

export const LinkingWrapper = styled.div`
.selected {
    :after {
        content: '';
        display: block;
        position: absolute;
        bottom: -5px;
        left: 10%;
        height: 5px;
        border-left: 50px solid black;
    }
}
ul {
    list-style-type:none;
    display: flex;
    align-items: center;
    justify-content: center;
    margin: 0;
}
li {
    display: inline-block;
    padding: 5px 5px 10px 5px;
    position: relative;
    font-weight: bold;
    margin: 10px 30px;
    font-size: 24px;
    font-weight: 100;
    a {
        color: black;
    }
}
`
export const Image = styled.image`
  img {
    width: 30px;
    height: 30px;
    margin-left: 5px;
  }
`
