import styled from 'styled-components';

export const HeroContainer = styled.div`
    display: flex;
`
export const Image = styled.image`
  img {
    width: 450px;
    height: 350px;
    margin-left: 5px;
    margin-top: 10px;
    border-radius:2px;
  }
`

export const Text = styled.p`
    margin-top: 15px;
`
