import styled from 'styled-components';

export const HeroContainer = styled.div`
    display: flex;
    border-radius: 3px;
    width:35%;
    background-color: #95A5A6 ;
    align-items:center;
    justify-content:center;
    margin: 10px auto;
`
export const Image = styled.image`
  img {
    width: 316px;
    height: 218px;
    margin-left: 5px;
    margin-top: 10px;
    border-radius: 3px;
  }
`

export const Text = styled.p`
    margin-top: 15px;
`
