import styled from 'styled-components';

export const HeroContainer = styled.div`
    display: flex;
    border-radius: 3px;
    width: 35%;
    background-color: #CACFD2;
    align-items: center;
    justify-content: center;
    margin: 20px auto;
`
export const Image = styled.image`
  img {
    width: 316px;
    height: 218px;
    margin-left: 5px;
    margin-top: 10px;
    border-radius: 3px;
  }
`

export const Text = styled.p`
    font-family: Avenir;
    margin-top: 15px;
    margin-left: 40px;
`
